// Przykładowy AI Assistant w n8n
// W rzeczywistości tutaj byś użył OpenAI, Anthropic, itp.

const message = $input.first().json.message;
const userName = $input.first().json.userName;
const userId = $input.first().json.userId;

// Przykładowa logika
let response = '';

if (message.toLowerCase().includes('help')) {
  response = `Hi ${userName}! I'm an AI Assistant. You can ask me anything and I'll try to help.`;
} else if (message.toLowerCase().includes('hello')) {
  response = `Hello ${userName}! Nice to meet you.`;
} else {
  // Tutaj mogłbyś integrować z OpenAI API
  response = `You said: "${message}" - Thank you for your message!`;
}

return {
  response: response,
  userId: userId,
  userName: userName,
  timestamp: new Date().toISOString()
};
